    0% {
      transform: translateY(200px);
    }
    100% {
      transform: translateY(0);
    }
  }

  #sign {
    transform-origin: 47% 35%;
    transform: rotate(-8deg);
    animation-timing-function: cubic-bezier(0.86, 0, 0.07, 1);
    animation: swing 6s infinite;
  }

  @keyframes swing {
    50% {
      transform: rotate(2deg);
    }
  }
</style>
